import React, { useState } from 'react'
import './SignUp.css';
import { Formik, Form, Field, ErrorMessage, useFormik } from 'formik';

const SignUp = () => {

    /*   const [IGN, setIGN] = useState("");
      const [userName, setuserName] = useState("");
      const [userPrenom, setuserPrenom] = useState("");
      const [userDate, setuserDate] = useState("");
      const [userEmail, setuserEmail] = useState("");
      const [userPassword, setuserPassword] = useState("");
      const [age, setage] = useState(0);
      const [avatar, setavatar] = useState("")
   */

    const [userData, setuserData] = useState({})

    const data = new FormData();

    const onhandleInput = e => {
        const id = e.target.id
        const obj = {}
        obj[id] = e.target.value

        setuserData({ obj })

        console.log(userData)

    }

    const formik = useFormik({
        initialValues: {



        },
        onSubmit: values => {
            data.append('IGN', values.IGN);
            data.append('userName', values.userName);
            data.append('userPrenom', values.userPrenom);
            data.append('userDate', values.userDate);
            data.append('userPassword', values.userPassword)
            data.append('userEmail', values.userEmail);
            data.append('age', values.age)
            for (var pair of data.entries()) {
                console.log(pair[0] + ', ' + pair[1]);
            }
        },
    });



    return (

        < div style={{
            backgroundImage: `url( ${process.env.PUBLIC_URL}/images/thumb-1920-406809.jpg)`,

            backgroundSize: `cover`,
        }
        }>
            <Formik
                initialValues={{
                    IGN: '',
                    userName: '',
                    userPrenom: '',
                    userPassword: '',
                    userDate: '',
                    userEmail: '',
                    age: 0,
                    avatar: null
                }}
                onSubmit={(values) => {
                    console.log(values)
                }}
            >


                <div className="container">
                    <h1 className=" text-center pt-4 text-warning text-stroke" style={{

                    }}> Register Now</h1>
                    <div style={{
                        backgroundColor: `rgba(0,0,0,0.5)`,
                        width: `1000px`,
                        margin: `auto`,
                        marginTop: `50px`
                    }}>
                        <div style={{
                            margin: `auto`,
                            color: `#03fcf0`,
                            fontWeight: `bolder`,
                            padding: `70px 70px`
                        }}>
                            <form class="row g-3" onSubmit={formik.handleSubmit}>
                                <div class="col-md-12 mb-4">
                                    <label htmlFor="IGN" class="form-label ">IGN</label>
                                    <input
                                        type="text"
                                        class="form-control"
                                        id="IGN"
                                        name="IGN"
                                        onChange={formik.handleChange}
                                        value={formik.values.IGN} />
                                </div>
                                <div class="col-md-12 mb-4"  >
                                    <label htmlFor="userName" class="form-label">UserName</label>
                                    <input
                                        type="text"
                                        class="form-control"
                                        id="userName"
                                        name="userName"
                                        onChange={formik.handleChange}
                                        value={formik.values.userName} />
                                </div>
                                <div class="col-md-12 mb-4"  >
                                    <label htmlFor="userPrenom" class="form-label">UserPrenom</label>
                                    <input
                                        type="text"
                                        class="form-control"
                                        id="userPrenom"
                                        name="userPrenom"
                                        onChange={formik.handleChange}
                                        value={formik.values.userPrenom}
                                    />
                                </div>
                                <div class="col-12 mb-4">
                                    <label htmlFor="userPassword" class="form-label">Password</label>
                                    <input
                                        type="password"
                                        class="form-control"
                                        id="userPassword"
                                        name="userPassword"
                                        onChange={formik.handleChange}
                                        value={formik.values.userPassword}
                                    />
                                </div>
                                <div class="col-12 mb-4">
                                    <label htmlFor="pwdConf" class="form-label">Confirm Password</label>
                                    <input type="password" class="form-control" id="pwdConf" />
                                </div>
                                <div class="col-md-12 mb-4">
                                    <label htmlFor="userDate" class="form-label">Date</label>
                                    <input
                                        type="date"
                                        class="form-control"
                                        id="userDate"
                                        name="userDate"
                                        onChange={formik.handleChange}
                                        value={formik.values.userDate}
                                    />
                                </div>

                                <div class="col-md-12 mb-4">
                                    <label htmlFor="userEmail" class="form-label">E-mail</label>
                                    <input
                                        type="email"
                                        class="form-control"
                                        id="userEmail"
                                        name="userEmail"
                                        onChange={formik.handleChange}
                                        value={formik.values.userEmail}
                                    />
                                </div>
                                <div class="col-md-12 mb-4">
                                    <label htmlFor="age" class="form-label">Age</label>
                                    <input
                                        type="number"
                                        class="form-control"
                                        id="age"
                                        name="age"
                                        onChange={formik.handleChange}

                                    />
                                </div>
                                <div class="col-md-12 mb-4">
                                    <label htmlFor="avatar" class="form-label">Avatar</label>
                                    <input
                                        type="file"
                                        class="form-control"
                                        id="avatar"
                                        name="avatar"

                                        onBlur={(e) => {
                                            data.append('avatar', e.target.files[0])
                                        }}


                                    />
                                </div>

                                <div class="col-12">
                                    <button type="submit" class="btn btn-primary">Register</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div >
            </Formik>
        </div >
    )
}

export default SignUp
