import axios from 'axios'
import React, { useEffect, useState } from 'react'

const Games = () => {
    const [games, setgames] = useState([])
    useEffect(() => {
        axios.get("http://localhost:5000/game")
            .then((res) => {
                setgames(res.data)
                console.log(games)
            })
            .catch(err => console.log(err))
    }, [])

    const gamesView = games.map((game) => (<div class="card col-md-4" style={{ width: `18rem` }}>
        <img class="card-img-top" src={process.env.PUBLIC_URL + '/images/gamesPhoto/' + game.gamePhoto} alt="Card image cap" />
        <div class="card-body">
            <h5 class="card-title">{game.gameName}</h5>
            <p class="card-text">{game.gameType}</p>
            <a href="#" class="btn btn-primary">See Tournaments</a>
        </div>
    </div>
    ))
    return (
        <div className="container">
            <div className="row" style={{
                marginTop: `50px`
            }}>
                {

                    gamesView
                }
            </div>
        </div >
    )
}

export default Games
