import axios from 'axios'
import React, { useEffect, useState } from 'react'

const TournamentDetails = (props) => {
    const [tournament, settournament] = useState({})
    const [participant, setparticipant] = useState([])
    useEffect(async () => {
        const response = await axios.get(`http://localhost:5000/tournament/findOne/${props.match.params.tournamentId}`)
        const data = response.data;
        settournament(data)
        setparticipant(data.participants)

        console.log(participant)
    }, [])


    const onJoinTournament = () => {
        const dataform = new FormData();
        axios.get(`http://localhost:5000/tournament/join/${props.match.params.tournamentId}`)

    }
    return (
        <div style={{
            backgroundImage: `url(http://localhost:5000/images/tournamentPhoto/${tournament.tournamentPic})`,
            height: `1000px`,
            backgroundRepeat: `no-repeat`,
            backgroundSize: `cover`,
            display: `flex`,
            flexDirection: `column`
        }}>
            <div style={{
                display: `flex`,
                flexDirection: `column`,
                textAlign: `center`,
                margin: `100px auto `
            }}>
                <div style={{
                    height: `300px`,
                    width: `600px`,
                    backgroundColor: `rgba(0,0,0,0.5)`,


                }}>
                    <h1 style={{
                        color: `yellow`
                    }}>{tournament.nameTournament}</h1>
                    <h2 style={{
                        color: `yellow`
                    }}>{tournament.gameOfTournament}</h2>
                    <h2 style={{
                        color: `white`
                    }}>{tournament.participantNumber}/ {tournament.numberOfPlayers}</h2>

                    <h3 style={{
                        color: `white`
                    }}>{tournament.dateByDate} -- {tournament.dateByTime}</h3>


                    <h3 style={{
                        color: `white`
                    }}>{tournament.price} $</h3>



                </div>

            </div>

            <div style={{
                margin: `-10px auto`,
                color: `white`,
                width: `600px`,
                textAlign: `center`
            }}>
                <h1 style={{
                    marginBottom: `50px`
                }}>JOIN TOURNAMENT NOW AND COMPETE FOR COOL PRIZES</h1>
                <button style={{
                    height: `50px`,
                    fontWeight: `bold`
                }} className="btn btn-warning"
                    onClick={onJoinTournament}
                >JOIN TOURNAMENT NOW </button>
            </div>
            <div>
                {
                    tournament.participants == 0 ? <h1 style={{
                        color: `white`,
                        textAlign: `center`,
                        padding: `50px`
                    }}>NO ONE IS JOINED YET TAKE YOUR PLACE AND COMPETE</h1> : <div>
                        <table class="table">
                            <thead class="thead-dark">
                                <tr>
                                    <th scope="col">Tournament Name</th>
                                    <th scope="col">Time</th>
                                    <th scope="col">Participant</th>
                                    <th scope="col">Price</th>
                                    <th scope="col">Details</th>

                                </tr>
                            </thead>
                            <tbody>
                                {
                                    participant.map((tour) => (
                                        <tr >
                                            <td scope="col">{tour.nameTournament}</td>

                                            <td>{tour.dateByDate} {tour.dateByTime} </td>
                                            <td>{tour.participantNumber}/ {tour.numberOfPlayers} </td>
                                            <td>{tour.price}</td>


                                        </tr>
                                    ))
                                }


                            </tbody>
                        </table>
                    </div>
                }
            </div>
        </div>
    )
}

export default TournamentDetails
