import React, { useEffect, useState } from 'react'
import {
    Nav, NavLink, Bars, NavMenu, NavBtn, NavBtnLink
} from "./Navbar"

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import { useHistory } from 'react-router';


const Navbar = (props) => {



    const accountStatus = props.connected ? (
        <div style={{
            display: `flex`
        }}>
            <div style={{
                display: `flex`
            }}>
                <NavLink to="/profile" activeStyle>
                    <h3>{props.user.IGN}</h3>
                    <img style={{
                        width: `100px`,
                        borderRadius: `50%`,
                        padding: `17px 17px`,

                    }} src={`http://localhost:5000/images/userAvatar/${props.user.avatar}`} />



                </NavLink>
            </div>
            <FontAwesomeIcon icon={faSignOutAlt} style={{
                margin: `auto`,
                height: `40px`,
                width: `100px`,
                color: `white`

            }}
                onClick={props.onlogOut} />
        </div>) : <NavBtn >
        <NavBtnLink to='/signin' > LogIn</NavBtnLink>
        <NavBtnLink to='/signup' > Register</NavBtnLink>

    </NavBtn>

    return (
        <>
            <Nav>

                <NavLink to="/">
                    <img src={process.env.PUBLIC_URL + `127dac7c14e1c9e49865e8cc0d661329.jpg`} style={{
                        width: `50px`,
                        borderRadius: `30px`,
                        backgroundColor: `violet`
                    }} />
                </NavLink>
                <Bars />
                <NavMenu>
                    <NavLink to="/" activeStyle>
                        Home
                    </NavLink>
                    <NavLink to="/tournaments" activeStyle>
                        Tournaments
                    </NavLink>
                    <NavLink to="/news" activeStyle>
                        News
                    </NavLink>
                    <NavLink to="/games" activeStyle>
                        Games
                    </NavLink>
                </NavMenu>

                {
                    accountStatus

                }


            </Nav>
        </>
    )
}

export default Navbar
