import axios from 'axios'
import React, { useEffect, useState } from 'react'

const TournamentDetails = (props) => {
    const [tournament, settournament] = useState({})
    useEffect(async () => {
        const response = await axios.get(`http://localhost:5000/tournament/findOne/${props.match.params.tournamentId}`)
        const data = response.data;
        settournament(data)
        console.log(tournament)
    }, [])
    return (
        <div style={{
            backgroundImage: `url(http://localhost:5000/images/tournamentPhoto/${tournament.tournamentPic})`,
            height: `1000px`,
            backgroundRepeat: `no-repeat`,
            backgroundSize: `cover`,
            display: `flex`,
            flexDirection: `column`
        }}>
            <div style={{
                display: `flex`,
                flexDirection: `column`,
                textAlign: `center`,
                margin: `100px auto `
            }}>
                <div style={{
                    height: `300px`,
                    width: `600px`,
                    backgroundColor: `rgba(0,0,0,0.5)`,


                }}>
                    <h1 style={{
                        color: `yellow`
                    }}>{tournament.nameTournament}</h1>
                    <h2 style={{
                        color: `yellow`
                    }}>{tournament.gameOfTournament}</h2>
                    <h2 style={{
                        color: `white`
                    }}>{tournament.participantNumber}/ {tournament.numberOfPlayers}</h2>

                    <h3 style={{
                        color: `white`
                    }}>{tournament.dateByDate} -- {tournament.dateByTime}</h3>


                    <h3 style={{
                        color: `white`
                    }}>{tournament.price} $</h3>



                </div>

            </div>

            <div style={{
                margin: ` auto`
            }}>
                <h1>JOIN TOURNAMENT NOW AND COMPETE FOR COOL PRIZES</h1>
                <button className="btn btn-warning">JOIN TOURNAMENT NOW </button>
            </div>
            <div>

            </div>
        </div>
    )
}

export default TournamentDetails
