import React from 'react'
import {
    Nav, NavLink, Bars, NavMenu, NavBtn, NavBtnLink
} from "./Navbar"

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCheckSquare, faCoffee, faSignOutAlt } from '@fortawesome/free-solid-svg-icons'
import { useHistory } from 'react-router';


const Navbar = () => {
    const history = useHistory();
    const onLogOut = () => {
        localStorage.removeItem('userData')
        history.push('./')

    }
    const user = JSON.parse(localStorage.getItem('userData'));
    const userNAme = (
        <div style={{
            display: `flex`
        }}>
            <div style={{
                display: `flex`
            }}>
                <NavLink to="/profile" activeStyle>
                    <h3>{user.IGN}</h3>
                    <img style={{
                        width: `100px`,
                        borderRadius: `50%`,
                        padding: `17px 17px`,

                    }} src={`http://localhost:5000/images/userAvatar/${user.avatar}`} />



                </NavLink>
            </div>
            <FontAwesomeIcon icon={faSignOutAlt} style={{
                margin: `auto`,
                height: `40px`,
                width: `100px`,
                color: `white`

            }}
                onClick={onLogOut} />
        </div>)

    return (
        <>
            <Nav>

                <NavLink to="/">
                    Logo
                </NavLink>
                <Bars />
                <NavMenu>
                    <NavLink to="/" activeStyle>
                        Home
                    </NavLink>
                    <NavLink to="/tournaments" activeStyle>
                        Tournaments
                    </NavLink>
                    <NavLink to="/news" activeStyle>
                        News
                    </NavLink>
                    <NavLink to="/games" activeStyle>
                        Games
                    </NavLink>
                </NavMenu>

                {
                    user ? userNAme :
                        <NavBtn >
                            <NavBtnLink to='/signin' > LogIn</NavBtnLink>
                            <NavBtnLink to='/signup' > Register</NavBtnLink>

                        </NavBtn>
                }


            </Nav>
        </>
    )
}

export default Navbar
