import React, { useState } from 'react'
import './SignUp.css';

const SignUp = () => {

    const [IGN, setIGN] = useState("");
    const [userName, setuserName] = useState("");
    const [userPrenom, setuserPrenom] = useState("");
    const [userDate, setuserDate] = useState("");
    const [userEmail, setuserEmail] = useState("");
    const [userPassword, setuserPassword] = useState("");
    const [age, setage] = useState(0);
    const [avatar, setavatar] = useState("")

    const data = new FormData();

    const onhandleInput = e => {


        console.log(e.target.id)

    }
    const onhandleuserName = e => {
        e.preventDefault()

        setuserName(e.target.value);

        console.log(userName)
    }
    const onhandleuserPrenom = e => {
        e.preventDefault()

        setuserPrenom(e.target.value);

        console.log(userPrenom)
    }
    const onhandleuserDate = e => {
        e.preventDefault()

        setuserDate(e.target.value);

        console.log(userName)
    }
    const onhandleuserEmail = e => {
        e.preventDefault()

        setuserName(e.target.value);

        console.log(userName)
    }
    const onhandleuserPassword = e => {
        e.preventDefault()

        setuserName(e.target.value);

        console.log(userName)
    }
    const onhandleage = e => {
        e.preventDefault()

        setuserName(e.target.value);

        console.log(userName)
    }

    const onhandleavatar = e => {
        e.preventDefault()

        setuserName(e.target.value);

        console.log(userName)
    }




    return (

        < div style={{
            backgroundImage: `url( ${process.env.PUBLIC_URL}/images/thumb-1920-406809.jpg)`,

            backgroundSize: `cover`,
        }
        }>
            <div className="container">
                <h1 className=" text-center pt-4 text-warning text-stroke" style={{

                }}> Register Now</h1>
                <div style={{
                    backgroundColor: `rgba(0,0,0,0.5)`,
                    width: `1000px`,
                    margin: `auto`,
                    marginTop: `50px`
                }}>
                    <div style={{
                        margin: `auto`,
                        color: `#03fcf0`,
                        fontWeight: `bolder`,
                        padding: `70px 70px`
                    }}>
                        <form class="row g-3">
                            <div class="col-md-12 mb-4">
                                <label htmlFor="IGN" class="form-label ">IGN</label>
                                <input type="text" class="form-control" id="IGN" onBlur={(e) => onhandleIGN(e)} />
                            </div>
                            <div class="col-md-12 mb-4"  >
                                <label htmlFor="userName" class="form-label">UserName</label>
                                <input type="text" class="form-control" id="userName" onBlur={(e) => onhandleuserName(e)} />
                            </div>
                            <div class="col-md-12 mb-4"  >
                                <label htmlFor="userPrenom" class="form-label">UserPrenom</label>
                                <input type="text" class="form-control" id="userPrenom" />
                            </div>
                            <div class="col-12 mb-4">
                                <label htmlFor="userPassword" class="form-label">Password</label>
                                <input type="password" class="form-control" id="userPassword" />
                            </div>
                            <div class="col-12 mb-4">
                                <label htmlFor="pwdConf" class="form-label">Confirm Password</label>
                                <input type="password" class="form-control" id="pwdConf" />
                            </div>
                            <div class="col-md-12 mb-4">
                                <label htmlFor="userDate" class="form-label">Date</label>
                                <input type="date" class="form-control" id="userDate" />
                            </div>

                            <div class="col-md-12 mb-4">
                                <label htmlFor="userEmail" class="form-label">E-mail</label>
                                <input type="email" class="form-control" id="userEmail" />
                            </div>
                            <div class="col-md-12 mb-4">
                                <label htmlFor="age" class="form-label">Age</label>
                                <input type="number" class="form-control" id="age" />
                            </div>
                            <div class="col-md-12 mb-4">
                                <label htmlFor="avatar" class="form-label">Avatar</label>
                                <input type="file" class="form-control" id="avatar" />
                            </div>
                            <div class="col-12 mb-4">
                                <div class="form-check">
                                    <input class="form-check-input" type="checkbox" id="gridCheck" />
                                    <label class="form-check-label" for="gridCheck">Check me out </label>
                                </div>
                            </div>
                            <div class="col-12">
                                <button type="submit" class="btn btn-primary">Sign in</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div >
        </div >
    )
}

export default SignUp
