    const onhandleuserPassword = e => {

    }
    const onhandleage = e => {

    }

    const onhandleavatar = e => {

    }


    return (

        < div style={{
            backgroundImage: `url( ${process.env.PUBLIC_URL}/images/thumb-1920-406809.jpg)`,

            backgroundSize: `cover`,
        }
        }>
            <div className="container">
                <h1 className=" text-center pt-4 text-warning text-stroke" style={{

                }}> Register Now</h1>
                <div style={{
                    backgroundColor: `rgba(0,0,0,0.5)`,
                    width: `1000px`,
                    margin: `auto`,
                    marginTop: `50px`
                }}>
                    <div style={{
                        margin: `auto`,
                        color: `#03fcf0`,
                        fontWeight: `bolder`,
                        padding: `70px 70px`
                    }}>
                        <form class="row g-3">
                            <div class="col-md-12 mb-4">
                                <label for="inputEmail4" class="form-label ">IGN</label>
                                <input type="text" class="form-control" id="IGN" onBlur={onhandleIGN} />
                            </div>
                            <div class="col-md-12 mb-4"  >
                                <label for="inputPassword4" class="form-label">UserName</label>
                                <input type="text" class="form-control" id="userName" />
                            </div>
                            <div class="col-md-12 mb-4"  >
                                <label for="inputPassword4" class="form-label">UserPrenom</label>
                                <input type="text" class="form-control" id="userPrenom" />
                            </div>
                            <div class="col-12 mb-4">
                                <label for="inputAddress" class="form-label">Password</label>
                                <input type="password" class="form-control" id="userPassword" />
                            </div>
                            <div class="col-12 mb-4">
                                <label for="inputAddress2" class="form-label">Confirm Password</label>
                                <input type="password" class="form-control" id="pwdConf" />
                            </div>
                            <div class="col-md-12 mb-4">
                                <label for="inputCity" class="form-label">Date</label>
                                <input type="date" class="form-control" id="userDate" />
                            </div>

                            <div class="col-md-12 mb-4">
                                <label for="inputZip" class="form-label">E-mail</label>
                                <input type="email" class="form-control" id="userEmail" />
                            </div>
                            <div class="col-md-12 mb-4">
                                <label for="inputZip" class="form-label">Age</label>
                                <input type="number" class="form-control" id="age" />
                            </div>
                            <div class="col-md-12 mb-4">
                                <label for="inputZip" class="form-label">Avatar</label>
                                <input type="file" class="form-control" id="avatar" />
                            </div>
                            <div class="col-12 mb-4">
                                <div class="form-check">
                                    <input class="form-check-input" type="checkbox" id="gridCheck" />
                                    <label class="form-check-label" for="gridCheck">Check me out </label>
                                </div>
                            </div>
                            <div class="col-12">
                                <button type="submit" class="btn btn-primary">Sign in</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div >
        </div >
    )
}

export default SignUp
