import axios from 'axios';
import { Formik, useFormik } from 'formik'
import React, { useState } from 'react'
import { useHistory } from 'react-router';

const CreateTournament = () => {
    const data = new FormData();
    const history = useHistory();
    const [tournamentPic, settournamentPic] = useState(null)




    const formik = useFormik({
        initialValues: {
            nameTournament: '',
            dateTournament: '',
            gameOfTournament: '',
            numberOfPlayers: '',
            price: ''



        },
        onSubmit: values => {
            data.append('nameTournament', values.nameTournament);
            data.append('dateTournament', values.dateTournament);
            data.append('gameOfTournament', values.gameOfTournament);
            data.append('numberOfPlayers', values.numberOfPlayers);
            data.append('price', values.price)

            data.append('tournamentPic', tournamentPic)
            console.log(tournamentPic)



            axios.post('http://localhost:5000/user/add', data)
                .then((res) => {
                    console.log(res)
                    history.push('/signin')
                })
                .catch((err) => console.log(err))
        },
    });


    return (
        < div style={{
            backgroundImage: `url( ${process.env.PUBLIC_URL}/images/thumb-1920-406809.jpg)`,

            backgroundSize: `cover`,
        }
        }>
            <Formik
            >

            </Formik>
            <div className="container">
                <h1 className=" text-center pt-4 text-warning text-stroke" style={{

                }}> Register Now</h1>
                <div style={{
                    backgroundColor: `rgba(0,0,0,0.5)`,
                    width: `1000px`,
                    margin: `auto`,
                    marginTop: `50px`
                }}>
                    <div style={{
                        margin: `auto`,
                        color: `#03fcf0`,
                        fontWeight: `bolder`,
                        padding: `70px 70px`
                    }}>
                        <form className="row g-3" onSubmit={formik.handleSubmit}>
                            <div className="col-md-12 mb-4">
                                <label htmlFor="nameTournament" className="form-label ">NameTournament</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    id="nameTournament"
                                    name="nameTournament"
                                    onChange={formik.handleChange}
                                    value={formik.values.nameTournament} />
                            </div>
                            <div className="col-md-12 mb-4"  >
                                <label htmlFor="dateTournament" className="form-label">dateTournament</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    id="dateTournament"
                                    name="dateTournament"
                                    onChange={formik.handleChange}
                                    value={formik.values.dateTournament} />
                            </div>
                            <div className="col-md-12 mb-4"  >
                                <label htmlFor="gameOfTournament" className="form-label">gameOfTournament</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    id="gameOfTournament"
                                    name="gameOfTournament"
                                    onChange={formik.handleChange}
                                    value={formik.values.gameOfTournament}
                                />
                            </div>
                            <div className="col-12 mb-4">
                                <label htmlFor="numberOfPlayers" className="form-label">numberOfPlayers</label>
                                <input
                                    type="password"
                                    className="form-control"
                                    id="numberOfPlayers"
                                    name="numberOfPlayers"
                                    onChange={formik.handleChange}
                                    value={formik.values.numberOfPlayers}
                                />
                            </div>
                            <div className="col-12 mb-4">
                                <label htmlFor="price" className="form-label">price</label>
                                <input type="number" className="form-control" id="price" />
                            </div>




                            <div className="col-md-12 mb-4">
                                <label htmlFor="avatar" className="form-label">Tournament Picture</label>
                                <input
                                    type="file"
                                    className="form-control"
                                    id="avatar"
                                    name="avatar"

                                    onChange={(e) => {
                                        settournamentPic(e.target.files[0])

                                    }}


                                />
                            </div>

                            <div className="col-12">
                                <button type="submit" className="btn btn-primary">Register</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div >
        </div >
    )
}

export default CreateTournament
