import React from 'react'
import GamesHome from './GamesHome/GamesHome';
import HeadHome from "./headHome/HeadHome";
import TournamentHome from './TournamentHome/TournamentHome';


const Home = () => {

    const TournamentsView = games.map((game) => (

        <div className="col-4">
            <div className="card" style={{ width: "25rem" }}>
                <img src={process.env.PUBLIC_URL + `/images/gamesPhoto/${game.gamePhoto}`} alt={game._id} className="card-img-top" />
                <div className="card-body">
                    <h5 className="card-title">{game.gameName}</h5>
                    <p className="card-text">{game.gameType}</p>
                    <Link to={`/tournaments/${game.gameName}`} className="btn btn-primary">Join Tournament Now</Link>
                </div>
            </div>
        </div >




    ))


    return (
        <div>
            <HeadHome />
            <GamesHome />
            <TournamentHome />
        </div>
    )
}

export default Home
