import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link, useRouteMatch } from 'react-router-dom'

const Tournaments = (props) => {
    let { path, url } = useRouteMatch();

    const [tournaments, settournaments] = useState([])
    useEffect(async () => {
        console.log()
        const response = await axios.get(`http://localhost:5000/tournament/`)
        const tr = await response.data
        settournaments(tr);
        console.log(tournaments)
    }, [])
    return (
        <div>



            <div>
                <div className="container"
                    style={{
                        display: `flex`,
                        flexDirection: `column`
                    }}>
                    <h1 style={{
                        textAlign: `center`,
                        margin: `auto`,
                        padding: `50px `

                    }}>Tournament For {props.match.params.game}</h1>
                    <table class="table">
                        <thead class="thead-dark">
                            <tr>
                                <th scope="col">Tournament Name</th>
                                <th scope="col">Tournament Game</th>

                                <th scope="col">Time</th>
                                <th scope="col">Participant</th>
                                <th scope="col">Price</th>
                                <th scope="col">Details</th>

                            </tr>
                        </thead>
                        <tbody>
                            {
                                tournaments.map((tour) => (
                                    <tr >
                                        <td scope="col">{tour.nameTournament}</td>
                                        <td scope="col">{tour.tournamentGame}</td>

                                        <td>{tour.dateByDate} {tour.dateByTime} </td>
                                        <td>{tour.participantNumber}/ {tour.numberOfPlayers} </td>
                                        <td>{tour.price}</td>
                                        <td className="btn btn-warning"><Link to={`./tournaments/${tour._id}`}>Incpect</Link></td>

                                    </tr>
                                ))
                            }


                        </tbody>
                    </table>
                </div>
            </div >

        </div>
    )
}

export default Tournaments
